func TestBinarySearch(t *testing.T){
	testValues:=[]struct{
		arr []int
		target int
		result int
	}{
		{[]int{4, 7, 10, 12, 16, 17, 55, 60, 61} ,61 ,8},
		{[]int{4, 7, 10, 12, 16, 17, 55, 60, 61},12,3},
	}
	for _,tt:=range testValues{

		res:=binarySearch(tt.arr,tt.target)
		if  res != tt.result{
			t.Errorf("While searching arr %v , tried to find %d , expected to get %d . Got instead %d",tt.arr,tt.target,tt.result,res)
		}
	}
}



